Int_t getHiBinFromhiHF(const Double_t hiHF)
{
	Int_t binPos=-1;
	for(int i=0;i<nBins;++i){
		if(hiHF>=binTable[i]&&hiHF<binTable[i+1]){
			binPos=i;
			break;
		}
	}
	binPos=nBins-1-binPos;

	return(Int_t)(200*((Double_t)binPos)/((Double_t)nBins));
}

